template.constructor:
WrapperLogger.record_call("#CLASSNAME", "#METHODNAME", #LISTPARAMS);
try {
  #RUN;
} catch (Throwable throwable) {
  WrapperLogger.record_throwable("#CLASSNAME", "#METHODNAME", throwable);
  #THROW throwable;
}

template.nonvoid:
WrapperLogger.record_call("#CLASSNAME", "#METHODNAME", #LISTPARAMS);
try {
  #RUN;
  WrapperLogger.record_val("#CLASSNAME", "#METHODNAME", #RETVAL);
} catch (Throwable throwable) {
  WrapperLogger.record_throwable("#CLASSNAME", "#METHODNAME", throwable);
  #THROW throwable;
}
return #WRAPPED_RETVAL;

template.void:
WrapperLogger.record_call("#CLASSNAME", "#METHODNAME", #LISTPARAMS);
try {
  #RUN;
} catch (Throwable throwable) {
  WrapperLogger.record_throwable("#CLASSNAME", "#METHODNAME", throwable);
  #THROW throwable;
}

template.extra:
private class WrappedIterator implements Iterator {
  private Iterator iterator;
  WrappedIterator(Iterator it) {
    iterator = it;
  }
  public boolean hasNext() {
    return iterator.hasNext();
  }
  public Object next() {
    return WrapperState.getWrapper(iterator.next());
  }
  public void remove() {
    iterator.remove();
  }
}

LockssDaemon theDaemon;

public WrappedPlugin() {}

public void setOriginal(Plugin plugin) throws InstantiationException {
  if (innerPlugin!=null && innerPlugin!=plugin) {
    throw new InstantiationException("Class " + innerPlugin.getClass().getName() + " already wrapped.");
  } else {
    innerPlugin = plugin;
    if (theDaemon!=null) {
      initPlugin(theDaemon);
    }
  }
}

template.special:
/* The Plugin Manager has an entry for the inner plugin,
   and will call it directly, so this method
   should do nothing */
if (innerPlugin!=null) {
  innerPlugin.stopPlugin();
}

if (obj instanceof WrappedPlugin) {
  return innerPlugin.equals(WrapperState.getOriginal(obj));
} else {
  return innerPlugin.equals(obj);
}

Collection coll = innerPlugin.getAllAus();
Collection wcoll = new ArrayList();
Iterator it = coll.iterator();
while (it.hasNext()) {
   WrappedArchivalUnit wau = (WrappedArchivalUnit)WrapperState.getWrapper(it.next());
   wcoll.add(wau);
}
return wcoll;
